    private void BmostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BmostrarActionPerformed
      
        int i=1,con=0,c=0;
        char x,y;
        Mosn=Nombre.getText();
        Le=Letra.getText();
        y= Le.charAt(0);
        c= Mosn.length();
        for(i=1 ; i <= c ; i++){
            x = Mosn.charAt(i-1);
            if ( x == y){
                con++;
                
            String complemento=Character.toString(x);
            Pf = Mosn.replace(complemento,"xyz");
            }else if(x != y){
                Nnom.setText("No Hay Resultados");
            }             
        }
        Tlet.setText(""+con);
        Nnom.setText(Pf);
    }//GEN-LAST:event_BmostrarActionPerformed

